import PySimpleGUI as sg
from ui.controller.controller import Controller
from ui.view_manager import ViewManager

class LightFieldGameViewController(Controller):
    """Implements the controller for the LightFieldGameView.
    """
    # Base64 representation of images for the buttons.
    light_on_base64 = b''
    light_off_base64 = b''
    
    def handle_event(self, event: str) -> None:
        
        if event == "Exit" or event == sg.WIN_CLOSED:
            ViewManager.terminate()
        elif type(event) == tuple:
            element = self.view.window[event]
            
            if element.metadata:
                element.update(image_data=LightFieldGameViewController.light_off_base64, image_size=element.ImageSize)
            else:
                element.update(image_data=LightFieldGameViewController.light_on_base64, image_size=element.ImageSize)
            element.metadata = not element.metadata
            
            self.view.read_events()
        else:
            self.view.read_events()
        